INSERT INTO `wp_wfNotifications` (`id`, `new`, `category`, `priority`, `ctime`, `html`, `links`) VALUES
('network-GB', 1, 'release630', 400, 1488265389, '<div style=\"padding:1rem 0px;\"><b>Announcing Wordfence 6.3.0 - Exciting Improvements</b><br>\nThis morning we are officially releasing Wordfence 6.3.0. If you have Wordfence auto-update enabled, your site may have already been updated automatically. This is a very exciting release for us with a host of improvements. The user interface has changed and we have added a new \'Dashboard\' menu option. You\'ll also notice the Wordfence menu structure has changed. <br><br>\n\nWe have tried to keep everything familiar, but easier to find and access. You can find the help documentation for the new dashboard <a href=\"https://docs.wordfence.com/en/Wordfence_Dashboard\">here</a>.<br><br>\n\n<a href=\"https://www.wordfence.com/blog/2017/01/wordfence-6-3-0/\">You can find the full announcement and details of the changes on our blog....</a></div>', '[]'),
('network-GC', 1, 'digest201701', 1000, 1488265389, '<div style=\"padding:1rem 0px;\">\n		<p style=\"font-weight:bold;font-size:0.8rem;color:#11967A;margin-top: 0;\">Recent Blog Highlights</p>\n		<div style=\"display:flex;flex-direction:row;max-width:100%;overflow-x:auto;flex-basis:auto;\">\n			<div style=\"margin: 0 0.5rem 1rem;padding: 1rem;border:1px solid #e9eff3;width:200px;flex-shrink:0;display:flex;flex-direction:column;margin-bottom:0.5rem;\">\n				<a href=\"https://www.wordfence.com/blog/2017/01/xmlrpc-wp-login-brute-force/?utm_source=plugin&amp;utm_campaign=bd020117&amp;utm_medium=pluginalert\" style=\"display:block;font-weight:bold;text-decoration:none;color:#00709e;margin-bottom:1rem;\">XMLRPC or WP-Login: Which do Brute Force Attackers Prefer</a>\n				<div style=\"font-size:0.9rem;color:#2d2d2d;\">A close look at brute force attack targets yielded some surprising results.</div>\n			</div>\n			<div style=\"margin: 0 0.5rem 1rem;padding: 1rem;border:1px solid #e9eff3;width:200px;flex-shrink:0;display:flex;flex-direction:column;margin-bottom:0.5rem;\">\n				<a href=\"https://www.wordfence.com/blog/2017/01/wordpress-security-plugin/?utm_source=plugin&amp;utm_campaign=bd020117&amp;utm_medium=pluginalert\" style=\"display:block;font-weight:bold;text-decoration:none;color:#00709e;margin-bottom:1rem;\">Do you Need a WordPress Security Plugin?</a>\n\n				<div style=\"font-size:0.9rem;color:#2d2d2d;\">This easy to follow post makes the clear case for running both a malware scanner and firewall on your WordPress site.</div>\n			</div>\n\n			<div style=\"margin: 0 0.5rem 1rem;padding: 1rem;border:1px solid #e9eff3;width:200px;flex-shrink:0;display:flex;flex-direction:column;margin-bottom:0.5rem;\">\n				<a href=\"https://www.wordfence.com/blog/2017/01/wordpress-botnet-monetization/?utm_source=plugin&amp;utm_campaign=bd020117&amp;utm_medium=pluginalert\" style=\"display:block;font-weight:bold;text-decoration:none;color:#00709e;margin-bottom:1rem;\">Analysis: Methods and Monetization of a Botnet Attacking WordPress</a>\n\n				<div style=\"font-size:0.9rem;color:#2d2d2d;\">\n				A deep dive on the methods and profit motive of an active Botnet.\n				</div>\n			</div>\n\n			<div style=\"margin: 0 0.5rem 1rem;padding: 1rem;border:1px solid #e9eff3;width:200px;flex-shrink:0;display:flex;flex-direction:column;margin-bottom:0.5rem;\">\n				<a href=\"https://www.wordfence.com/blog/2017/01/chrome-56-ssl-https-wordpress/?utm_source=plugin&amp;utm_campaign=bd020117&amp;utm_medium=pluginalert\" style=\"display:block;font-weight:bold;text-decoration:none;color:#00709e;margin-bottom:1rem;\">Imminent: Non-HTTPS Sites Labeled “Not Secure” by Chrome</a>\n\n				<div style=\"font-size:0.9rem;color:#2d2d2d;\">\n				An overview of changes to Google Chrome that will impact non-https site owners.\n				</div>\n			</div>\n\n			<div style=\"margin: 0 0.5rem 1rem;padding: 1rem;border:1px solid #e9eff3;width:200px;flex-shrink:0;display:flex;flex-direction:column;margin-bottom:0.5rem;\">\n				<a href=\"https://www.wordfence.com/blog/2017/01/gmail-phishing-data-uri/?utm_source=plugin&amp;utm_campaign=bd020117&amp;utm_medium=pluginalert\" style=\"display:block;font-weight:bold;text-decoration:none;color:#00709e;margin-bottom:1rem;\">Wide Impact: Highly Effective Gmail Phishing Technique Being Exploited</a>\n\n				<div style=\"font-size:0.9rem;color:#2d2d2d;\">\n				We profile a new phishing technique that is fooling even experienced technical users.\n				</div>\n			</div>\n\n			<div style=\"margin: 0 0.5rem 1rem;padding: 1rem;border:1px solid #e9eff3;width:200px;flex-shrink:0;display:flex;flex-direction:column;margin-bottom:0.5rem;\">\n				<a href=\"https://www.wordfence.com/blog/2017/01/how-wordpress-firewall-works/?utm_source=plugin&amp;utm_campaign=bd020117&amp;utm_medium=pluginalert\" style=\"display:block;font-weight:bold;text-decoration:none;color:#00709e;margin-bottom:1rem;\">How the Wordfence Firewall Works</a>\n\n				<div style=\"font-size:0.9rem;color:#2d2d2d;\">\n				We explain how the Wordfence firewall protects your website from attacks.\n				</div>\n			</div>\n\n			<div style=\"margin: 0 0.5rem 1rem;padding: 1rem;border:1px solid #e9eff3;width:200px;flex-shrink:0;display:flex;flex-direction:column;margin-bottom:0.5rem;\">\n				<a href=\"https://www.wordfence.com/blog/2017/01/december-2016-wordpress-attack-activity-report/?utm_source=plugin&amp;utm_campaign=bd020117&amp;utm_medium=pluginalert\" style=\"display:block;font-weight:bold;text-decoration:none;color:#00709e;margin-bottom:1rem;\">The December 2016 WordPress Attack Activity Report</a>\n\n				<div style=\"font-size:0.9rem;color:#2d2d2d;\">\n				Stats and insights from Wordfence Network from December.\n				</div>\n                </div>\n		</div>\n	</div><script type=\"application/javascript\">jQuery(\'.wf-dashboard-item-list-title\').css(\'min-width\', 0);</script>', '[]'),
